事务操作：
Database db = new Database("dbkey");
SqlConnection conn = new SqlConnection(db.ConnectionString);
conn.Open();
using (SqlTransaction trans = conn.BeginTransaction()) {
    try {
        db.ExecuteNonQuery(trans,);
        db.ExecuteNonQuery(trans,);
        trans.Commit();
    } catch (Exception ex) {
        trans.Rollback();
        throw ex;
    }
}
conn.Close();

